INSERT INTO INSTRUMENT(INSTRUMENT_NAME,INSTRUMENT_DESCRIPTION, PRICE) VALUES('Сковородка','Сковорода́ — приспособление для жарки пищи. Имеет, как правило, круглую форму. Может быть без ручек, с одной ручкой или с двумя ручками. Изготавливается из чугуна, нержавеющей стали или стали, покрытой различными эмалями, алюминия, керамики и др. материалов.',1500),('Стакан','Стака́н — обычно стеклянный сосуд, близкий по форме к цилиндру или усечённому конусу, без ручки и без ножки. Применяется для холодных и горячих напитков. В последнем случае часто используется подстаканник.',50),('Микроволновка','Микроволновая печь — бытовой прибор, быстрота нагревания в котором обеспечивается использованием микроволн сверхвысоких частот.',5000),('Тарелка','Таре́лка — плоское керамическое изделие различной формы с приподнятыми краями, предназначенное для сервировки стола[2], вид столовой посуды.',50),('Кухонная Плита','Кухонная плита — нагревательный прибор для приготовления пищи. Кроме варочной поверхности с конфорками кухонная плита часто имеет встроенный духовой шкаф и дополнительные отделения.',10000),('Чайник','Ча́йник — полое изделие различной формы с крышкой, ручкой и носиком, предназначенное для кипячения воды и заваривания чая',3000),('Кастрюля','Кастрюля - металлическая посуда для варки пищи.',1000)
INSERT INTO SHOP(SHOP_NAME,SHOP_DESCRIPTION,RATING) VALUES('Магнит','«Магнит» — российская холдинговая компания, занимающаяся розничной торговлей через сеть магазинов «Магнит». В июле 2019 года под управлением компании: 14 507 магазинов формата у дома и сити, 467 супермаркетов и гипермаркетов, 5 523 дрогери, 800 аптек, 2 алкомаркета, 23 магазина формата опт.', 4.3),('Аура Богемии','',4.5)
INSERT INTO COUNTRY(COUNTRY_NAME,COUNTRY_DESCRIPTION) VALUES('Россия','Ру́сская ку́хня — традиционная кухня русского народа. Природные особенности мест проживания — обширные леса и долгие зимы — обусловили многие характерные особенности традиционной русской кухни: разнообразие горячих супов и каш, квашений, мочений и солений, грибных блюд и дичи на русском столе.'),('Америка','Американская кухня сегодня — это симбиоз десятка различных национальных кухонь мира. Но начиналась она с традиционной английской кухни 18 в. и кухни североамериканских индейцев.'),('Международное','Блюда в данной категории не принадлежат культуре определенной страны')
INSERT INTO DISH(DISH_NAME,RECIPE) VALUES('Яичница с сыром','В разогретой сковороде разбить яйца, сверху посыпать специями и щепоткой соли. Выложить порезанный кубиками сыр. Жарить до готовности. Очень вкусный, слегка итальянский завтрак. :) Приятного аппетита!'),('Кофе с молоком','Засыпьте растворимый кофе в стакан. Разогрейте воду и залейте кипятком. Добавьте молока по вкусу.'),('Молоко','Налейте молоко в стакан'),('Омлет','Разбить яйца в миску, взболтать их вилкой, влить молоко, перемешать, посолить. Подогреть сковороду с маслом (можно и без, если жарите на тефлоне), влить яйца и жарить минут 5 на медленном огне. При желании можно перемешать омлет, чтобы он поджарился равномерно. Или накрыть его крышкой и подержать минуты 2-3 - так он станет пышным.'),('Горячие бутерброды','Нарежьте хлеб на тарелку, сверху положите колбасу и сыр. Поставьте в микроволновку на две минуты. Готово!'),('Жаренный сендвич с сыром','Намажьте два ломтика хлеба сливочным маслом. Положите один на холодную сковороду маслом вниз. Сверху положите кусочек сыра, колбаски, снова сыр, помажьте горчицей и положите сверху второй ломтик хлеба маслом вверх. Включите огонь на плите и жарьте 5 минут, после чего переверните и жарьте 2 минуты. Готово!'),('Вареные яца','Если положить яйца в холодную воду, то нужно дождаться закипания, убавить огонь и варить в течение 2 минут ')
